table = {    {"crosshair", "cameraCrosshair"},}addEventHandler( "onClientResourceStart", resourceRoot,function()    for i = 1, #table do        local shader, tec = dxCreateShader ( "texreplace.fx" )        local tex = dxCreateTexture ( ""..table[i][1]..".png" )        engineApplyShaderToWorldTexture ( shader, table[i][2] )        engineApplyShaderToWorldTexture ( shader, table[i][2].."lod" )        dxSetShaderValue ( shader, "gTexture", tex )    endend)  addEventHandler ( "onClientResourceStart", resourceRoot,function () setPlayerHudComponentVisible ("crosshair", true ) end) 